__author__ = 'ray'


import bisect

class BearChairs(object):
  # atLeast -> tuple
  # d -> int
  # ret -> tuple
  def findPositions(self, atLeast, d):
    n = len(atLeast)
    if n<2: return atLeast
    bi = []
    ret = []
    for v in atLeast:
      ret.append(self.insert(bi,v,d))
    return tuple(ret)

  def insert(self,bi,v,d):
    bisect.insort(bi,v)
    _i = bisect.bisect(bi,v)-1
    # if violate with previous bear or next bear
    l = r = 0
    if _i-1>=0 and bi[_i-1]+d>v:
      l,r = _i-1,_i
    if _i+1<len(bi) and bi[_i+1]-d<v:
      l,r = _i,_i+1
    if l or r:
      del bi[bisect.bisect(bi,v)-1]
      while r<len(bi):
        if bi[r]-bi[l]>=2*d:
          return self.insert(bi,bi[l]+d,d)
        l += 1
        r += 1
      return self.insert(bi,bi[l]+d,d)
    return v


